mapper.update:
update clazz
name = #{name}, room = #{room}, begin_date = #{beginDate}, end_date=#{endDate}, master_id=#{masterId},
subject = #{subject}
where id = #{id}

mapper.select:
select t1.*,t2.name masterName from clazz t1 left join emp t2 on t1.master_id=t2.id
t1.name like concat('%', #{name}, '%') and t1.end_date between #{begin} and #{end}
order by t1.update_time desc
